import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-confirmation-modal',
  templateUrl: './confirmation-modal.component.html',
  styleUrls: ['./confirmation-modal.component.scss'],
})
export class ConfirmationModalComponent {
  @Input() patient!: string;
  @Output() confirm = new EventEmitter<void>();
  @Output() cancel = new EventEmitter<void>();

  confirmDelete() {
    this.confirm.emit();
  }

  cancelDelete() {
    this.cancel.emit();
  }
}
<div class="overlay">
  <div class="app__modal">
    <p>Are you sure you want to delete {{patient}}?</p>
    <div class="d-flex gap-3 mt-3">
      <button (click)="confirmDelete()" type="button" class="btn btn-danger">Confirm</button>
      <button (click)="cancelDelete()" type="button" class="btn btn-primary">Cancel</button>
    </div>
  </div>
</div>
