import { Component, EventEmitter, Input, Output } from '@angular/core';
import { DynamicFields } from 'src/app/models/patient.model';

@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss']
})
export class RegistrationFormComponent {
  @Input() isModalOpen: boolean = false;
  @Output() isModalOpenChange = new EventEmitter<boolean>();

  newPatient: DynamicFields  = {
    id: '',
    firstName: '',
    lastName: '',
    personalId: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    medicalConditions: [],
    surgicalHistory: [],
    medications: [],
    allergies: [],
    immunizations: [],
    doctorsName: '',
    doctorsSurname: '',
    insurance: null,
  };

  patientFields: string[] = [
    'first name',
    'last name',
    'personal id',
    'date of birth',
    'phone number',
    'email',
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
    `doctor's name`,
    `doctor's surname`,
    'insurance',
  ];

  canBeEmptyFields: string[] = [
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
  ];

  openModal() {
    this.resetForm();
    this.isModalOpenChange.emit(true);
  }

  close() {
    this.resetForm();
    this.isModalOpenChange.emit(false);
  }

  preventClose(event: Event) {
    event.stopPropagation();
  }

  private resetForm() {
    this.newPatient = {
      id: '',
      firstName: '',
      lastName: '',
      personalId: '',
      dateOfBirth: '',
      phoneNumber: '',
      email: '',
      medicalConditions: [],
      surgicalHistory: [],
      medications: [],
      allergies: [],
      immunizations: [],
      doctorsName: '',
      doctorsSurname: '',
      insurance: null,
    };
  }

  registerPatient() {
    // Implement the registration logic here
    // You can send the 'newPatient' object to your API or service
    // to store the patient data in your backend.
    console.log(this.newPatient); // Just for testing
    this.close(); // Close the modal after registration
  }
}
