import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Patient } from 'src/app/models/patient.model';
import { PatientsService } from 'src/app/services/patients.service';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.scss'],
})
export class TableComponent {
  @Input() title?: string;
  @Input() tableDescription?: string;
  @Input() data?: Patient[];
  @Input() head?: string[];
  @Input() properties?: string[];
  @Input() isDoctorName?: boolean;
  @Input() isModalOpen: boolean = false;

  @Output() passPatient = new EventEmitter<Patient>();

  constructor(private patientsService: PatientsService) { }

  getProperty(item: any, prop: string): any {
    return item[prop as keyof Patient];
  }

  openModal(item: Patient) {
    this.isModalOpen = true;
    this.passPatient.emit(item);
  }


  deletePatient(item: Patient) {
    this.patientsService.delete

  }

  onDeleteClick(event: Event, item: Patient) {
    event.stopPropagation();

  }
}
<div class="m-3">
  <h1 class="mb-3">{{ title }}</h1>
  <small id="tableDescription">{{ tableDescription }}</small>
  <table aria-describedby="tableDescription" class="table" *ngIf="data?.length; else noData">
    <thead class="patients__table">
      <tr>
        <th *ngFor="let headName of head">{{ headName }}</th>
      </tr>
    </thead>
    <tbody>
      <tr (click)="openModal(item)" class="table__row" *ngFor="let item of data">
        <td>{{ item.firstName + " " + item.lastName }}</td>
        <td *ngFor="let prop of properties">{{ getProperty(item, prop)}}</td>
        <td [innerHTML]="item.insurance | boolRepresentation"></td>
        <ng-container *ngIf="isDoctorName">
          <td>{{ item.doctorsName + " " + item.doctorsSurname }}</td>
        </ng-container>
        <ng-container *ngIf="isDoctorName">
          <td (click)="onDeleteClick($event, item)">delete</td>
        </ng-container>
      </tr>
    </tbody>
  </table>
  <ng-template #noData>
    <p>No data found</p>
  </ng-template>
</div>
