@Pipe({
  name: 'boolRepresentation',
})
export class BoolRepresentationPipe implements PipeTransform {
  transform(value: boolean): string {
    if (value) {
      return ✔;
    }
    return '<span style="color: red;">✘</span>';
  }
}
