import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Patient } from 'src/app/models/patient.model';
import { PatientsService } from 'src/app/services/patients.service';

@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss'],
})
export class RegistrationFormComponent {
  @Input() isModalOpen: boolean = false;
  @Output() isModalOpenChange = new EventEmitter<boolean>();

  newPatient: Patient = {
    id: '',
    firstName: '',
    lastName: '',
    personalId: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    medicalConditions: [],
    surgicalHistory: [],
    medications: [],
    allergies: [],
    immunizations: [],
    doctorsName: '',
    doctorsSurname: '',
    insurance: false,
  };

  patientFields: string[] = [
    'first name',
    'last name',
    'personal id',
    'date of birth',
    'phone number',
    'email',
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
    `doctor's name`,
    `doctor's surname`,
  ];

  canBeEmptyFields: string[] = [
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
  ];

  openModal() {
    this.resetForm();
    this.isModalOpenChange.emit(true);
  }

  close() {
    this.resetForm();
    this.isModalOpenChange.emit(false);
  }

  preventClose(event: Event) {
    event.stopPropagation();
  }

  private resetForm() {
    this.newPatient = {
      id: '',
      firstName: '',
      lastName: '',
      personalId: '',
      dateOfBirth: '',
      phoneNumber: '',
      email: '',
      medicalConditions: [],
      surgicalHistory: [],
      medications: [],
      allergies: [],
      immunizations: [],
      doctorsName: '',
      doctorsSurname: '',
      insurance: false,
    };
  }
  patientForm: FormGroup;
  constructor(
    private patientsService: PatientsService,
    private formBuilder: FormBuilder
  ) {
    // Initialize the patientForm with validation rules
    this.patientForm = this.formBuilder.group({
      // Define form controls and add validation rules
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      // Add more fields with validators as needed
    });
  }

  registerPatient() {
    if (this.patientForm.valid) {
      // If the form is valid, submit the data
      this.patientsService.addNewPatient(this.newPatient).subscribe({
        next: (response) => {
          console.log('Patient registered successfully:', response);
          this.close();
        },
        error: (error) => {
          console.error('Error registering patient:', error);
        },
      });
    } else {
      // If the form is invalid, show an error message
      alert('Please fill in all the required fields.');
    }
  }
}
<div (click)="close()" class="form__modal-overlay" *ngIf="isModalOpen">
  <div class="form__modal" (click)="preventClose($event)">
    <form (ngSubmit)="registerPatient()" #patientForm="ngForm">
      <div class="fields">
        <div class="form-group">
          <label for="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            class="form-control"
            [(ngModel)]="newPatient.firstName"
            required
          />
          <small
            *ngIf="
              patientForm.controls.firstName.invalid &&
              patientForm.controls.firstName.touched
            "
            class="text-danger"
          >
            This field is required.
          </small>
        </div>
        <div class="form-group">
          <label for="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            class="form-control"
            [(ngModel)]="newPatient.lastName"
            required
          />
          <small
            *ngIf="
              patientForm.controls.lastName.invalid &&
              patientForm.controls.lastName.touched
            "
            class="text-danger"
          >
            This field is required.
          </small>
        </div>
        <div class="form-group">
          <label for="dateOfBirth">Date of Birth</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            class="form-control"
            [(ngModel)]="newPatient.dateOfBirth"
            required
          />
          <small
            *ngIf="
              patientForm.controls.dateOfBirth.invalid &&
              patientForm.controls.dateOfBirth.touched
            "
            class="text-danger"
          >
            This field is required.
          </small>
        </div>

        <div class="form-group d-flex flex-column">
          <label for="insurance" class="form-check-label">Insurance</label>
          <input
            type="checkbox"
            id="insurance"
            name="insurance"
            class="form-check-input checkbox"
            [(ngModel)]="newPatient.insurance"
          />
        </div>
      </div>
      <div class="d-flex gap-3 mt-3">
        <button (click)="close()" type="button" class="btn btn-danger">
          Close
        </button>
        <button type="submit" class="btn btn-primary">Register</button>
      </div>
    </form>
  </div>
</div>
