import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Patient } from 'src/app/models/patient.model';

@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss']
})
export class RegistrationFormComponent {
  @Input() isModalOpen: boolean = false;
  @Output() isModalOpenChange = new EventEmitter<boolean>();

  newPatient: Patient = {
    id: '',
    firstName: '',
    lastName: '',
    personalId: '',
    dateOfBirth: '',
    phoneNumber: '',
    email: '',
    medicalConditions: [],
    surgicalHistory: [],
    medications: [],
    allergies: [],
    immunizations: [],
    doctorsName: '',
    doctorsSurname: '',
    insurance: false,
  };

  patientFields: string[] = [
    'firstName',
    'lastName',
    'personalId',
    'dateOfBirth',
    'phoneNumber',
    'email',
    'medicalConditions',
    'surgicalHistory',
    'medications',
    'allergies',
    'immunizations',
    'doctorsName',
    'doctorsSurname',
    'insurance',
  ];

  openModal() {
    this.resetForm();
    this.isModalOpenChange.emit(true);
  }

  close() {
    this.resetForm();
    this.isModalOpenChange.emit(false);
  }

  preventClose(event: Event) {
    event.stopPropagation();
  }

  private resetForm() {
    this.newPatient = {
      id: '',
      firstName: '',
      lastName: '',
      personalId: '',
      dateOfBirth: '',
      phoneNumber: '',
      email: '',
      medicalConditions: [],
      surgicalHistory: [],
      medications: [],
      allergies: [],
      immunizations: [],
      doctorsName: '',
      doctorsSurname: '',
      insurance: false,
    };
  }
  getPatient(newPatient: any, prop: string): any {
    return newPatient[prop as keyof Patient];
  }
  registerPatient() {
    // Implement the registration logic here
    // You can send the 'newPatient' object to your API or service
    // to store the patient data in your backend.
    console.log(this.newPatient); // Just for testing
    this.close(); // Close the modal after registration
  }
}
