import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Patient } from 'src/app/models/patient.model';
import { PatientsService } from 'src/app/services/patients.service';

@Component({
  selector: 'app-registration-form',
  templateUrl: './registration-form.component.html',
  styleUrls: ['./registration-form.component.scss'],
})
export class RegistrationFormComponent implements OnInit {
  @Input() isModalOpen: boolean = false;
  @Output() isModalOpenChange = new EventEmitter<boolean>();

  registerPatientForm: FormGroup = this.fb.group({});

  buidForm(): void {
    this.registerPatientForm = this.fb.group({
      'first name': ['', Validators.required],
      'last name': ['', Validators.required],
      'personal id': ['', Validators.required],
      'date of birth': ['', Validators.required],
      'phone number': ['', Validators.required],
      email: ['', Validators.required],
      'medical conditions': [''],
      'surgical history': [''],
      medications: [''],
      allergies: [''],
      immunizations: [''],
      "doctor's name": ['', Validators.required],
      "doctor's surname": ['', Validators.required],
      insurance: [false],
    });
  }

  ngOnInit(): void {
    this.buidForm();
  }

  patientFields: string[] = [
    'first name',
    'last name',
    'personal id',
    'phone number',
    'email',
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
    `doctor's name`,
    `doctor's surname`,
  ];

  openModal() {
    this.resetForm();
    this.isModalOpenChange.emit(true);
  }

  close() {
    this.resetForm();
    this.isModalOpenChange.emit(false);
  }

  preventClose(event: Event) {
    event.stopPropagation();
  }

  private resetForm() {
    this.registerPatientForm.reset();
  }

  constructor(
    private patientsService: PatientsService,
    private fb: FormBuilder
  ) {}

  registerPatient() {
    const patient: Partial<Patient> = {
      firstName: this.registerPatientForm.get('first name')?.value,
      lastName: this.registerPatientForm.get('last name')?.value,
      personalId: this.registerPatientForm.get('personal id')?.value,
      dateOfBirth: this.registerPatientForm.get('date of birth')?.value,
      phoneNumber: this.registerPatientForm.get('phone number')?.value,
      email: this.registerPatientForm.get('email')?.value,
      medicalConditions:
        this.registerPatientForm.get('medical conditions')?.value,
      surgicalHistory: this.registerPatientForm.get('surgical history')?.value,
      medications: this.registerPatientForm.get('medications')?.value,
      allergies: this.registerPatientForm.get('allergies')?.value,
      immunizations: this.registerPatientForm.get('immunizations')?.value,
      doctorsName: this.registerPatientForm.get(`doctor's name`)?.value,
      doctorsSurname: this.registerPatientForm.get(`doctor's surname`)?.value,
      insurance: this.registerPatientForm.get('insurance')?.value,
    };
    if (this.registerPatientForm.valid) {
      this.patientsService.addNewPatient(patient).subscribe({
        next: () => {
          this.close();
          this.resetForm();
        },
        error: (error) => {
          console.error('Error registering patient:', error);
        },
      });
    } else {
      alert('Please fill in all the required fields.');
    }
  }
}
<div (click)="close()" class="form__modal-overlay" *ngIf="isModalOpen">
  <div class="form__modal" (click)="preventClose($event)">
    <form [formGroup]="registerPatientForm" (ngSubmit)="registerPatient()">
      <div class="fields">
        <div *ngFor="let field of patientFields" class="form-group">
          <label [for]="field">{{ field }}</label>
          <input
            type="text"
            [id]="field"
            [formControlName]="field"
            class="form-control"
            [placeholder]="field === 'date of birth' ? 'DD.MM.YYYY' : ''"
          />
          <small
            *ngIf="
              registerPatientForm.get(field)?.hasError('required') &&
              registerPatientForm.get(field)?.touched
            "
            class="text-danger"
          >
            This field is required.
          </small>
        </div>
        <!-- Date of Birth input-->
        <div class="form-group">
          <label for="date of birth">Date of Birth</label>
          <input
            type="date"
            id="date of birth"
            [formControlName]="'date of birth'"
            class="form-control"
          />
          <small
            *ngIf="
              registerPatientForm.get('date of birth')?.hasError('required') &&
              registerPatientForm.get('date of birth')?.touched
            "
            class="text-danger"
          >
            This field is required.
          </small>
        </div>
        <!-- Insurance input-->
        <div class="form-group d-flex flex-column">
          <label for="insurance" class="form-check-label">Insurance</label>
          <input
            type="checkbox"
            id="insurance"
            [formControlName]="'insurance'"
            class="form-check-input checkbox"
          />
        </div>
      </div>
      <div class="d-flex gap-3 mt-3">
        <button (click)="close()" type="button" class="btn btn-danger">
          Close
        </button>
        <button type="submit" class="btn btn-primary">Register</button>
      </div>
    </form>
  </div>
</div>
