import { Component, OnInit, ViewChild } from '@angular/core';
import { RegistrationFormComponent } from 'src/app/components/registration-form/registration-form.component';
import { Patient } from 'src/app/models/patient.model';
import { PatientsService } from 'src/app/services/patients.service';

@Component({
  selector: 'app-all-patients',
  templateUrl: './all-patients.component.html',
  styleUrls: ['./all-patients.component.scss'],
})
export class AllPatientsComponent implements OnInit {
  @ViewChild(RegistrationFormComponent) modal!: RegistrationFormComponent;
  patients: Patient[] = [];
  head: string[] = [
    'patient',
    'personal id',
    'date of birth',
    'phone number',
    'email',
    'medical conditions',
    'surgical history',
    'medications',
    'allergies',
    'immunizations',
    'insurance',
    'doctor',
  ];

  patientProperties: string[] = [
    'personalId',
    'dateOfBirth',
    'phoneNumber',
    'email',
    'medicalConditions',
    'surgicalHistory',
    'medications',
    'allergies',
    'immunizations',
  ];

  isModalOpen: boolean = false;

  constructor(private patientsService: PatientsService, private renderer: Renderer2) {}

  ngOnInit(): void {
    this.patientsService.getAllPatients().subscribe({
      next: (patients) => {
        this.patients = patients;
      },
      error: (err) => {
        console.log(err);
      },
    });
  }
  openModal() {
    this.isModalOpen = true;
    const modalComponent: RegistrationFormComponent = this.modal;
    if (modalComponent) {
      modalComponent.openModal();
    }
  }
}
